import React,{Component} from 'react'
import {connect} from 'react-redux'
import {Action_creator_add_Reminder} from '../Action Creators/index'
class App extends Component {
    state={
        text:"",
        date:new Date()
    }
    render(){
        console.log(this.props);
        return(
            <div>Hello from App
            <h2>What Should U DO?</h2>
            <input
            onChange={(e)=>this.setState({text:e.target.value})}
            className="form-control" type="text" placeholder="Enter What You Think....?"/>
            <input
            onChange={(e)=>this.setState({date:e.target.value})}
            className="form-control" type="datetime-local" placeholder="Enter What You Think....?"/>
            <button 
            onClick={()=>this.props.Action_creator_add_Reminder(this.state.text,this.state.date)}
            className="btn btn-primary btn-block">Add Reminder</button>
            <button className="btn btn-block btn-danger">Cleare Reminders</button>
            </div>
        )
    }
}
//connect action creator with reducer {Action_creator_add_Reminder}
//to connect reducer with app 
export default connect(state =>
{

    return {
        reminders : state
    }
},{Action_creator_add_Reminder}) (App);
