import React , {Component} from 'react'
class Addnew extends Component{
    state={
        name:"",
        age:""
    }
    changing = (e)=>
    {
        this.setState(
        {
            [e.target.id]:e.target.value
          //ma3nah en ha3mel set swa2 lel name aw age 3ala 7asab el id 

        })
    }
    submiting = (e) =>
    {
        e.preventDefault();
        console.log(this.setState);
    }

    render()
    {
        return(
            <form onSubmit={this.submiting}>
                <input type="text" id="name" placeholder="Enter Name......" onChange={this.changing}/>
                <input type="number" id="age" placeholder="Enter Age....." onChange={this.changing}/>
                <input type="submit" name="Add" />
            </form>
        )
    }
}
export default Addnew;
