import React , {Component} from 'react';
import Abdo from './abdo';
import Func from './function';
class App extends Component{
  state = {
    name : "bo2la"
  }
  handlechange = (e) =>
  {
     this.setState({
       name:e.target.value
     })
  }
  render()
  {


    return(
      <div className="App">
        <form><input type="text" onChange={this.handlechange}/></form>

         <Abdo name={this.state.name}/>
         <Func details={this.state.name} age={this.state.age}/>
      </div>
    )
  }
}
export default App;
